let v = IN[0];
if v % 2 == 0 {              // 偶数？
    v                        // そのまま
} else {
    v + 1                    // 奇数なら +1
}

//-----------------------------------------------------------
// IN[0] : 頂点数  (実数でも OK → floor で整数化)
// IN[1] : 半径 r
//-----------------------------------------------------------
let n = floor(IN[0]);          // 頂点数を整数化
let r = IN[1];

let pts = [];                  // ここに Point を詰める
let i   = 0;                   // ループカウンタ

while i < n {
    let ang = 6.28318530718 * i / n;   // = 2π * i / n
    let x = r * cos(ang);
    let y = r * sin(ang);
    pts.push([x, y, 0]);   // 単なる数値配列の点を追加
    i = i + 1;
}

pts                           // ← リストをそのまま出力
